#[aoc_generator(day1)]
fn generator_input(input: &str) -> String {
    input
        .lines()
        .next()
        .expect("I know you're there")
        .to_string()
}

#[aoc(day1, part1)]
fn part1(line: &str) -> Option<i16> {
    process(line).last().cloned()
}

#[aoc(day1, part2)]
fn part2(line: &str) -> usize {
    process(line)
        .iter()
        .position(|x| *x == -1)
        .expect("Could not find element")
        + 1
}

#[aoc(day1, part2, alt1=brute)]
fn part2_alt(line: &str) -> Option<usize> {
    let mut running_total: i16 = 0;
    for (i, c) in line.chars().enumerate() {
        if c == '(' {
            running_total += 1;
        } else {
            running_total += -1;
        }
        if running_total == -1 {
            return Some(i + 1);
        }
    }
    None
}

fn process(input: &str) -> Vec<i16> {
    let mut running_total: i16 = 0;
    input
        .chars()
        .map(|c| {
            if c == '(' {
                running_total += 1;
                running_total
            } else {
                running_total += -1;
                running_total
            }
        })
        .collect()
}

#[test]
fn test_process() {
    assert_eq!(vec![-1, -2, -3], process(")))"));
    assert_eq!(vec![-1, 0, -1, -2, -1, -2, -3], process(")())())"));
    assert_eq!(vec![1, 2, 1, 2, 3, 2, 3], process("(()(()("));
}

